package main

import(
  "fmt"
  "time"
  "strings"
  "strconv"
)
var NUM []string = strings.Split("", "")

func largestProductinaSeries(n int) int64{
  var maxP int64 = 0
  var temp int64 = 1
  for i := 0; i < len(NUM) - n; i++{
    temp = 1
    for _, val := range NUM[i:i+n]{
      integer, _ := strconv.Atoi(val)
      temp *= int64(integer)
    }
    if(temp > maxP){
      maxP = temp
    }
  }
  return maxP
}

func main(){
  start := time.Now()

  fmt.Println(largestProductinaSeries(13))

  elapsed := time.Since(start)
  fmt.Printf("Time taken: %s\n", elapsed)
}
