public class HelloWorld{
  public static void main(String []args){
    System.out.println(largestProductinaSeries(13));      
  }

  public static long largestProductinaSeries(int n) {
  String s = "";
         long largProduct = 0;

        for (int i = 0; i < s.length() - n; i ++) {
            long product = 1;
            for (int j = i; j < i + n; j++) {
                product *= Integer.parseInt(s.substring(j, j + 1));
            }
            if (product > largProduct){ 
              largProduct = product;
            }  
        }
               return largProduct;
    }
}